import React, { useContext } from "react";
import "./Login.css";
import {
  Input,
  FormHelperText,
  FormControl,
  Button,
  SnackBar,
  InputLabel,
} from "@material-ui/core";
import { useState } from "react";
import { AuthContext } from "../../contexts/AuthContext";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [emailError, setErrorForEmail] = useState(false);
  const [passwordError, setErrorForPassword] = useState(false);

  const [invalidEmail, setInvalidEmail] = useState(false);

  const [invalidPassword, setInvalidPassword] = useState(false);

  const { dispatch } = useContext(AuthContext);

  const loginURL = "http://localhost:8080/auth/login";

  //use this flag to set to true/false then call the API only if true
  // this flag will be set to false if the email/password is invalid
  let flag = false;

  const emailPattern =
    /^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\\.,;:\s@"]{2,})$/i;

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleEmailPatternValidate = () => {
    if (!emailPattern.exec(email)) {
      setInvalidEmail(true);
      flag = false;
    } else {
      setInvalidEmail(false);
      flag = true;
    }
  };
  const handlePattternErrorValidate = () => {
    //uses state
    //   const [emailError, setErrorForEmail] = useState(false);
    //   const [passwordError, setErrorForPassword] = useState(false);
    email == "" ? setErrorForEmail(true) : setErrorForEmail(false);
    password == "" ? setErrorForPassword(true) : setErrorForPassword(false);
  };

  const handleLogin = (e) => {
    if (e) {
      e.preventDefault();
    }
    const encodedEmailAndPassword = window.btoa(`${email}:${password}`);

    handleBlankLoginPassword();
    handlePattternErrorValidate();

    if (!flag) {
      handleEmailPatternValidate();
    }
  };

  return (
    <div className="login-container">
      <form autoComplete="off">
        <FormControl variant="standard" required>
          <InputLabel htmlFor="username">Email</InputLabel>
          <Input
            id="email"
            type="email"
            onChange={handleEmailChange}
            value={email}
          >
            <div>
              {email.length >= 1 && invalidEmail === true && (
                <FormHelperText id="invalid-error">
                  Enter Valid Email
                </FormHelperText>
              )}
            </div>
          </Input>
        </FormControl>
        <br />
        <br />
        <FormControl variant="standard" required>
          <InputLabel htmlFor="password">Password</InputLabel>
          <Input id="password" type="password"></Input>
        </FormControl>
        <br />
        <br />
        <Button variant="contained" color="primary" type="submit">
          LOGIN
        </Button>
      </form>
    </div>
  );
};

export default Login;
