import "./Header.css";
import React, { useState, useContext } from "react";
import {
  Typography,
  Button,
  Tabs,
  Tab,
  Paper,
  CardContent,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Logo from "../../assets/logo.jpeg";
import { AuthContext } from "../../contexts/AuthContext";
import Modal from "react-modal";
import TabContainer from "../tabContainer/TabContainer";

Modal.setAppElement("#root");

const useStyles = makeStyles((theme) => ({
  card: {
    width: 400,
    padding: theme.spacing(2),
  },
}));

const Header = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  //used to get the userToken and if any dispatch
  //the action to be sent to the reducder which will caseswitch
  const { userToken, dispatch } = useContext(AuthContext);

  const handleLogin = () => {
    setModalOpen(true);
  };

  const handleLogout = () => {
    setModalOpen(false);
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  function a11yProps(index) {
    return {
      id: `full-width-tab-${index}`,
      "aria-controls": `full-width-tabpanel-${index}`,
    };
  }

  return (
    <header className="header-container">
      <img className="header-logo" src={Logo} alt="Logo" />
      <Typography variant="h6" className="header-title-text">
        Doctor Finder
      </Typography>
      {!userToken || modalOpen ? (
        <div className="header-buttons">
          <Button
            variant="contained"
            color="primary"
            className="header-login-button"
            onClick={handleLogin}
          >
            Login
          </Button>
        </div>
      ) : (
        <div className="header-buttons">
          <Button
            variant="contained"
            color="primary"
            className="header-logout-button"
            onClick={handleLogout}
          >
            Logout
          </Button>
        </div>
      )}
      <Modal
        isOpen={modalOpen}
        onRequestClose={handleModalClose}
        className="modal-container"
      >
        <Paper className="paper-container">
          <Typography variant="h6" className="modal-title">
            Authentication
          </Typography>
          <CardContent>
            <Tabs
              value={activeTab}
              onChange={handleTabChange}
              indicatorColor="secondary"
              textColor="primary"
              centered
            >
              <Tab label="LOGIN" {...a11yProps(0)} />
              <Tab label="REGISTER" {...a11yProps(1)} />
            </Tabs>
            <TabContainer value={activeTab} index={0}>
              Login
            </TabContainer>
            <TabContainer value={activeTab} index={1}>
              Register
            </TabContainer>
          </CardContent>
        </Paper>
      </Modal>
    </header>
  );
};

export default Header;
