import React, { useContext } from "react";
import "./Login.css";
import {
  Input,
  FormHelperText,
  FormControl,
  Button,
  SnackBar,
  InputLabel,
} from "@material-ui/core";
import { useState } from "react";
import { AuthContext } from "../../contexts/AuthContext";
import { set } from "date-fns";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [ErrorForInvalidEmail, setErrorForInvalidEmail] = useState(false);

  const { dispatch } = useContext(AuthContext);

  const loginURL = "http://localhost:8080/auth/login";

  //use this flag to set to true/false then call the API only if true
  // this flag will be set to false if the email/password is invalid
  let flag = false;

  const emailPattern =
    /^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\\.,;:\s@"]{2,})$/i;

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleEmailPatternValidate = () => {
    if (!emailPattern.exec(email)) {
      setErrorForInvalidEmail(true);
      flag = false;
    } else {
      setErrorForInvalidEmail(false);
      flag = true;
    }
  };
  const handleBlankEmailPassword = () => {
    email == ""
      ? setErrorForInvalidEmail(true)
      : setErrorForInvalidEmail(false);
    password == ""
      ? setErrorForInvalidPassword(true)
      : setErrorForInvalidPassword(false);
  };

  const handleLogin = (e) => {
    if (e) {
      e.preventDefault();
    }
    const encodedEmailAndPassword = window.btoa(`${email}:${password}`);
    if (!flag) {
      handleEmailPatternValidate();
    }
  };

  return (
    <div className="login-container">
      <form autoComplete="off">
        <FormControl variant="standard" required>
          <InputLabel htmlFor="username">Email</InputLabel>
          <Input id="email" type="email" onChange={handleEmailChange}></Input>
        </FormControl>
        <br />
        <br />
        <FormControl variant="standard" required>
          <InputLabel htmlFor="password">Password</InputLabel>
          <Input id="password" type="password"></Input>
        </FormControl>
        <br />
        <br />
        <Button variant="contained" color="primary" type="submit">
          LOGIN
        </Button>
      </form>
    </div>
  );
};

export default Login;
